from colorama import Fore

class Color:
    
    LACK        = Fore.BLACK
    RED         = Fore.RED
    RED_LIGHT   = Fore.LIGHTRED_EX
    GREEN       = Fore.GREEN
    GREEN_LIGHT = Fore.LIGHTGREEN_EX
    YELLOW      = Fore.YELLOW
    BLUE        = Fore.BLUE
    MAGENTA     = Fore.MAGENTA
    CYAN        = Fore.CYAN
    WHITE       = Fore.WHITE
    RESET       = Fore.RESET